import React from "react";
import "./addGrocery.css";

function AddGrocery({ addItem, value, setValue }) {
  return (
    <div>
      <form onSubmit={addItem}>
        <input
          className="bigInput"
          type="text"
          placeholder="Add Grocery"
          value={value}
          onChange={(e) => setValue(e.target.value)}
        />
        <button className="bigInput" onClick={addItem}>
          Add
        </button>
      </form>
    </div>
  );
}

export default AddGrocery;
