import React from "react";
import AddGrocery from "./AddGrocery/AddGrocery";
import ShowList from "./ShowList/ShowList";
import { useState } from "react";

function Grocery() {
  const [groceryList, setGroceryList] = useState([
    { text: "kur", completed: true, editMode: true },
    { text: "putka", completed: true, editMode: true },
    { text: "mandarina", completed: false, editMode: false },
  ]);
  const [value, setValue] = useState("");

  const addAll = (e) => {
    e.preventDefault();
    const items = [
      ...groceryList,
      { text: value, completed: true, editMode: true },
    ];
    setGroceryList(items);
    setValue("");
  };

  const deleteItem = (index) => {
    const deleter = [...groceryList];
    deleter.splice(index, 1);
    setGroceryList(deleter);
  };

  const complete = (index) => {
    const completer = [...groceryList];
    completer[index].completed = !completer[index].completed;

    setGroceryList(completer);
  };

  const changeItem = (index) => {
    const changeItemer = [...groceryList];
    changeItemer[index].editMode = !changeItemer[index].editMode;
    setGroceryList(changeItemer);
  };

  const changeSingle = (e, index) => {
    e.preventDefault();
    const items = [...groceryList];
    items[index].text = e.target.value;
    setGroceryList(items);
    setValue("");
  };

  return (
    <>
      <AddGrocery value={value} setValue={setValue} addItem={addAll} />
      <ShowList
        groceries={groceryList}
        deleteItem={deleteItem}
        complete={complete}
        changeItem={changeItem}
      />
    </>
  );
}

export default Grocery;
