import React from "react";

function ShowList({
  groceries,
  deleteItem,
  complete,
  changeItem,
  changeSmallText,
  backToNormal,
  cancelProcess,
  theOkish,
}) {
  return (
    <div className="ShowList">
      <h1>This is Showing list</h1>
      <ul>
        {groceries.map((grocy, index) => {
          if (grocy.editMode) {
            return (
              <li key={index}>
                <input
                  type="text"
                  value={grocy.value}
                  onChange={(e) => changeSmallText(e.target.value, index)}
                />
                <button onClick={() => cancelProcess(index)}>X</button>
                <button onClick={() => theOkish(e.target.value, index)}>
                  oK
                </button>
              </li>
            );
          } else {
            return (
              <li
                value={grocy.text}
                key={index}
                style={{ color: grocy.completed ? "red" : "green" }}
              >
                <p onDoubleClick={() => changeItem(index)}> {grocy.text}</p>

                <button onClick={() => deleteItem(index)}>X</button>
                <button onClick={() => complete(index)}>Complete</button>
                <button onClick={() => changeItem(index)}>edit</button>
              </li>
            );
          }
        })}
      </ul>
    </div>
  );
}

export default ShowList;
