import React from "react";
import "./SaveList.css";
import Button from "@material-ui/core/Button";

import DeleteIcon from "@material-ui/icons/Save";
import SaveIcon from "@material-ui/icons/Save";
import PlaylistAddCheckIcon from "@material-ui/icons/PlaylistAddCheck";
import { withStyles } from "@material-ui/core/styles";

const SaveButton = withStyles((theme) => ({
  root: {
    color: "#FEFEFE",
    fontWeight: "bold",
    backgroundColor: "rgb(172, 145, 94)",
    "&:hover": {
      backgroundColor: "#F7BC56",
    },
  },
}))(Button);

const CheckButton = withStyles((theme) => ({
  root: {
    color: "#FEFEFE",
    fontWeight: "bold",
    backgroundColor: "rgb(64, 167, 73)",
    "&:hover": {
      backgroundColor: "rgb(83, 211, 94)",
    },
  },
}))(Button);

const SaveList = ({
  saverValue,
  setsaverValue,
  saveUpShit,
  checkAll,
  uncheckAll,
  clearAll,
}) => {
  return (
    <div className="saveList">
      <div>
        <input
          maxLength="20"
          type="text"
          value={saverValue}
          onChange={(e) => setsaverValue(e.target.value)}
        />

        <SaveButton
          size="small"
          variant="contained"
          onClick={saveUpShit}
          startIcon={<SaveIcon />}
        >
          Save
        </SaveButton>
      </div>
      <div>
        <CheckButton
          startIcon={<PlaylistAddCheckIcon />}
          size="small"
          variant="contained"
          onClick={checkAll}
        >
          Check all
        </CheckButton>
        <Button size="small" variant="contained" onClick={uncheckAll}>
          Uncheck all
        </Button>
        <Button
          size="small"
          variant="contained"
          color="secondary"
          startIcon={<DeleteIcon />}
          onClick={clearAll}
        >
          Clear List
        </Button>
      </div>
    </div>
  );
};

export default SaveList;
