import React from "react";
import AddGrocery from "./AddGrocery/AddGrocery";
import ShowList from "./ShowList/ShowList";
import { useState } from "react";

function Grocery() {
  const [groceryList, setGroceryList] = useState(["kur", "putka", "mandarina"]);
  const [value, setValue] = useState("");

  const addAll = (e) => {
    e.preventDefault();
    const items = [...groceryList, value];
    setGroceryList(items);
    setValue("");
  };

  const deleteItem = (index) => {
    const items = [...groceryList];
    items.splice(index, 1);
    setGroceryList(items);
  };
  return (
    <>
      <AddGrocery value={value} setValue={setValue} addItem={addAll} />
      <ShowList groceries={groceryList} deleteItem={deleteItem} />
    </>
  );
}

export default Grocery;
