import React from "react";
import "./SaveList.css";
import Button from "@material-ui/core/Button";
import Icon from "@material-ui/core/Icon";
import DeleteIcon from "@material-ui/icons/Save";
import SaveIcon from "@material-ui/icons/Save";

import {
  createMuiTheme,
  withStyles,
  makeStyles,
  ThemeProvider,
} from "@material-ui/core/styles";

const ColorButton = withStyles((theme) => ({
  root: {
    color: theme.palette.getContrastText("black"),
    backgroundColor: "red",
    "&:hover": {
      backgroundColor: "red",
    },
  },
}))(Button);

const SaveList = ({
  saverValue,
  setsaverValue,
  saveUpShit,
  checkAll,
  uncheckAll,
  clearAll,
}) => {
  return (
    <div className="saveList">
      <div>
        <input
          maxLength="20"
          type="text"
          value={saverValue}
          onChange={(e) => setsaverValue(e.target.value)}
        />
        <Button
          size="small"
          variant="contained"
          onClick={saveUpShit}
          startIcon={<SaveIcon />}
        >
          Save
        </Button>
      </div>
      <div>
        <Button size="small" variant="contained" onClick={checkAll}>
          Check all
        </Button>
        <Button size="small" variant="contained" onClick={uncheckAll}>
          Uncheck all
        </Button>
        <Button
          size="small"
          variant="contained"
          color="secondary"
          startIcon={<DeleteIcon />}
          onClick={clearAll}
        >
          Clear List
        </Button>
      </div>
    </div>
  );
};

export default SaveList;
