import React from "react";
import "./ShowList.css";
import "font-awesome/css/font-awesome.min.css";
import Fade from "@material-ui/core/Fade";
import { makeStyles } from "@material-ui/core/styles";

function ShowList({
  groceries,
  deleteItem,
  complete,
  changeItem,
  changeSmallText,
  thisisCancel,
  submitSmallText,
}) {
  if (groceries.length === 0)
    return <h1 className="basich1">Your list is empty...</h1>;

  return (
    <div className="ShowList">
      <h1>Items added in {groceries.length} :</h1>
      <Fade>
        {groceries.map((grocy, index) => {
          if (grocy.editMode) {
            return (
              <div className="theItems editMode" key={index}>
                <input
                  className="smallInput"
                  type="text"
                  value={grocy.value}
                  onChange={(e) => changeSmallText(e.target.value, index)}
                  autoFocus
                />
                <div>
                  <button
                    className="fa fa-times-circle"
                    onClick={() => thisisCancel(index)}
                  ></button>
                  <button
                    className="fa fa-check-circle-o"
                    onClick={() => submitSmallText(index)}
                  ></button>
                </div>
              </div>
            );
          } else {
            return (
              <div
                className="theItems"
                value={grocy.text}
                key={index}
                style={{
                  textDecoration: grocy.completed ? "line-through" : "none",
                }}
              >
                <p onDoubleClick={() => changeItem(index)}> {grocy.text}</p>
                <div>
                  <button
                    className="fa fa-trash"
                    onClick={() => deleteItem(index)}
                  ></button>
                  <button
                    className="fa fa-check"
                    onClick={() => complete(index)}
                  ></button>
                  <button onClick={() => changeItem(index)}>edit</button>
                </div>
              </div>
            );
          }
        })}
      </Fade>
    </div>
  );
}

export default ShowList;
