import React from "react";
import "./Toggler.css";

import Accordion from "@material-ui/core/Accordion";
import AccordionSummary from "@material-ui/core/AccordionSummary";
import AccordionDetails from "@material-ui/core/AccordionDetails";
import Typography from "@material-ui/core/Typography";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import Grid from "@material-ui/core/Grid";

function Toggler({ toggle, dateItems, addbackToShowList }) {
  return (
    <div
      style={{
        display: toggle ? "block" : "none",
      }}
      className="theToggler"
    >
      {dateItems.map((key, index) => {
        return (
          <Accordion key={index}>
            <AccordionSummary
              expandIcon={<ExpandMoreIcon />}
              style={{
                backgroundColor: "#4caf50",
                boxShadow:
                  "0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23)",
              }}
            >
              <Grid m={1}>
                <Grid>
                  <Typography
                    variant="body1"
                    align="left"
                    style={{ color: "white" }}
                  >
                    {key.date}
                  </Typography>
                </Grid>
                <Grid alignSelf="flex-end">
                  <Typography
                    align="right"
                    style={{ color: "white" }}
                    variant="button"
                  >
                    {key.name}
                  </Typography>
                </Grid>
              </Grid>
            </AccordionSummary>
            {key.items.map((zac, index) => {
              return (
                <AccordionDetails key={index}>{zac.text}</AccordionDetails>
              );
            })}
            <div>
              <button onClick={() => addbackToShowList(index)}>Use</button>
            </div>
          </Accordion>
        );
      })}
    </div>
  );
}

export default Toggler;
