import React from "react";
import "./ShowList.css";
import "font-awesome/css/font-awesome.min.css";

function ShowList({
  groceries,
  deleteItem,
  complete,
  changeItem,
  changeSmallText,
  thisisCancel,
  submitSmallText,
}) {
  if (groceries.length === 0)
    return <h1 className="basich1">Your list is empty...</h1>;

  return (
    <div className="ShowList">
      <h1>Items left to shop :</h1>

      {groceries.map((grocy, index) => {
        let counterMe = null;
        if (grocy.completed) {
          counterMe = counterMe + 1;
        }
        console.log(counterMe);
        if (grocy.editMode) {
          return (
            <div className="theItems editMode" key={index}>
              <input
                className="smallInput"
                type="text"
                value={grocy.value}
                onChange={(e) => changeSmallText(e.target.value, index)}
                autoFocus
              />
              <div>
                <button
                  className="fa fa-times-circle"
                  onClick={() => thisisCancel(index)}
                ></button>
                <button
                  className="fa fa-check-circle-o"
                  onClick={() => submitSmallText(index)}
                ></button>
              </div>
            </div>
          );
        } else {
          return (
            <div
              className="theItems"
              value={grocy.text}
              key={index}
              style={{ color: grocy.completed ? "red" : "green" }}
            >
              <p onDoubleClick={() => changeItem(index)}> {grocy.text}</p>
              <div>
                <button
                  className="fa fa-trash"
                  onClick={() => deleteItem(index)}
                ></button>
                <button
                  className="fa fa-check"
                  onClick={() => complete(index)}
                ></button>
                <button onClick={() => changeItem(index)}>edit</button>
              </div>
            </div>
          );
        }
      })}
    </div>
  );
}

export default ShowList;
