import React from "react";
import "./Toggler.css";
import { makeStyles } from "@material-ui/core/styles";
import Accordion from "@material-ui/core/Accordion";
import AccordionSummary from "@material-ui/core/AccordionSummary";
import AccordionDetails from "@material-ui/core/AccordionDetails";
import Typography from "@material-ui/core/Typography";
import ExpandMoreIcon from "@material-ui/icons/ExpandMore";
import Collapse from "@material-ui/core/Collapse";

function Toggler({ toggle, dateItems }) {
  return (
    <div
      style={{
        display: toggle ? "block" : "none",
      }}
      className="theToggler"
    >
      <Collapse in={true} timeout={5000}>
        {dateItems.map((key, index) => {
          return (
            <Accordion>
              <AccordionSummary
                id="panel1a-header"
                expandIcon={<ExpandMoreIcon />}
                style={{
                  backgroundColor: "#4caf50",
                  boxShadow:
                    "0 10px 20px rgba(0, 0, 0, 0.19), 0 6px 6px rgba(0, 0, 0, 0.23)",
                }}
              >
                <Typography>{key.date}</Typography>
              </AccordionSummary>
              {key.items.map((zac, index) => {
                return (
                  <AccordionDetails key={index}>{zac.text}</AccordionDetails>
                );
              })}
            </Accordion>
          );
        })}
      </Collapse>
    </div>
  );
}

export default Toggler;
