import React from "react";
import AddGrocery from "./AddGrocery/AddGrocery";
import ShowList from "./ShowList/ShowList";
import { useState } from "react";

function Grocery() {
  const [groceryList, setGroceryList] = useState([
    { text: "kur", completed: true },
    { text: "putka", completed: true },
    { text: "mandarina", completed: false },
  ]);
  const [value, setValue] = useState("");

  const addAll = (e) => {
    e.preventDefault();
    const items = [...groceryList, value];
    setGroceryList(items);
    setValue("");
  };

  const deleteItem = (index) => {
    const pradnq = [...groceryList];
    pradnq.splice(index, 1);
    setGroceryList(pradnq);
  };

  const complete = (index) => {
    const pradnq = [...groceryList, { completed: false }];

    setGroceryList(pradnq);
  };

  return (
    <>
      <AddGrocery value={value} setValue={setValue} addItem={addAll} />
      <ShowList
        groceries={groceryList}
        deleteItem={deleteItem}
        complete={complete}
      />
    </>
  );
}

export default Grocery;
