import React from "react";

function ShowList({
  groceries,
  deleteItem,
  complete,
  changeItem,
  changeSingle,
}) {
  return (
    <div className="ShowList">
      <h1>This is Showing list</h1>
      <ul>
        {groceries.map((grocy, index) => {
          if (grocy.editMode) {
            return (
              <li
                value={grocy.text}
                key={index}
                style={{ color: grocy.completed ? "red" : "green" }}
                onDoubleClick={() => changeItem(index)}
              >
                {grocy.text}
                <button onClick={() => deleteItem(index)}>X</button>
                <button onClick={() => complete(index)}>Complete</button>
                <button onClick={() => changeItem(index)}>edit</button>
              </li>
            );
          } else {
            return (
              <li key={index}>
                <input value={grocy.text} onChange={() => changeSingle} />
                <button>X</button>
                <button>oK</button>
              </li>
            );
          }
        })}
      </ul>
    </div>
  );
}

export default ShowList;
